import { AdRequest } from "../../../types";
export type BannerAdDimension = "600x160" | "300x250";
export interface BannerAdConfig {
    dimensions?: BannerAdDimension;
}
export interface BannerAdRequest extends AdRequest {
}
export type BannerAdSuccessResponse = {
    success: true;
    message: string;
    data: {
        id: string;
        ctaUrl?: string;
        dimensions: {
            width: string;
            height: string;
        };
        mediaUrl: string;
    };
};
export type BannerAdFailureResponse = {
    success: false;
    message: string;
    data: {
        id?: string;
        ctaUrl?: string;
        dimensions: {
            width: string;
            height: string;
        };
        mediaUrl?: string;
    };
};
export type BannerAdResponse = BannerAdSuccessResponse | BannerAdFailureResponse;
